interface UserInfoI {
  biography: string;
  birthday: string;
  email: string;
  gender: string;
  lastname: string;
  name: string;
  phone: string;
  photo: string | null;
}

const ProfileUser = () => {
  const [fileList, setFileList] = useState<any[]>([]);
  const [imageUrl2, setImageUrl2] = useState<any>();
  const [user, setUser] = useState<UserInfoI>();

  const { id } = useParams();

  useEffect(() => {
    if (id) loadInfoUser(id);
  }, []);

  const loadInfoUser = async (id: string) => {
    try {
      const res = await clientAxios.get<UserInfoI[]>(`user/${id}`, {
        headers: headerAuthToken(),
      });
      console.log(res.data);
      setUser(res.data[0]);
      imageUrl2(res.data[0].photo);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <>
      <HeaderNav />
      <div style={{ height: 'calc(100vh - 80px)', background: '#fafafb' }}>
        <div className='content-register'>
          {user ? (
            <div className='content-register-row '>
              <h1>Perfil</h1>

              <>
                <Row gutter={50}>
                  <Col span={6} flex={1}>
                    <div className='upload-image'>
                      <Image
                        preview={false}
                        src={imageUrl2 ? imageUrl2 : 'error'}
                        alt='avatar'
                        width={200}
                        height={200}
                        fallback='data:image/png;base64,'
                      />
                    </div>
                  </Col>
                  <Col span={18} flex={1}>
                    <Row gutter={50}>
                      <Col span={12} flex={1}>
                        <p className='label'>Nombre</p>
                        <p className='text-profile-info'>{user.name}</p>
                      </Col>

                      <Col span={12}>
                        <p className='label'>Apellido</p>
                        <p className='text-profile-info'>{user.lastname}</p>
                      </Col>

                      <Col span={12}>
                        <p className='label'>Correo</p>
                        <p className='text-profile-info'>{user.email}</p>
                      </Col>

                      <Col span={12}>
                        <p className='label'>Teléfono</p>
                        <p className='text-profile-info'>{user.phone}</p>
                      </Col>

                      <Col span={12}>
                        <p className='label'>Fecha de nacimiento</p>
                        <p className='text-profile-info'>{user.birthday}</p>
                      </Col>

                      <Col span={12}>
                        <p className='label'>Biografía</p>
                        <p className='text-profile-info'>{user.biography}</p>
                      </Col>
                    </Row>
                  </Col>
                </Row>
              </>
            </div>
          ) : null}
        </div>
      </div>
    </>
  );
};

export default ProfileUser;
